import React, { Component } from 'react';
import { Helmet } from 'react-helmet';
// import './home.css'

import { Container, Row, Col } from 'react-bootstrap';

export default class Home extends Component
{	
  render()
  {
		return (
      <>
        <Helmet defer={false}>
          <title>Sebastian S. Rodriguez</title>
        </Helmet>

        <Row>
        <Col className="d-flex position-absolute flex-column align-items-center justify-content-center w-100 h-100">
              <img src="../../images/common/me2021.jpg" class="mr-4 rounded-circle" width="250" height="250" alt="Sebastian Rodriguez" /> 
              <h1>Sebastian S(amuel) Rodriguez</h1>
              <h4>srodri44 [at] illinois [dot] edu</h4>
              <p>
              <a target="_blank" rel="noreferrer" href="./docs/cv.pdf"><img src="https://img.icons8.com/ios/96/000000/resume.png" alt="Download CV" width="32" height="32" title="CV" /></a>
              <a target="_blank" rel="noreferrer" href="https://github.com/srodriguez1850"><img src="https://img.icons8.com/material-outlined/192/000000/github.png" alt="GitHub" width="32" height="32" title="GitHub" /></a>
              <a target="_blank" rel="noreferrer" href="https://scholar.google.com/citations?user=RdD2ZP8AAAAJ"><img src="https://img.icons8.com/material-outlined/96/000000/google-scholar.png" alt="Google Scholar" width="32" height="32" title="Google Scholar" /></a>
              <a target="_blank" rel="noreferrer" href="https://www.linkedin.com/in/srodriguez1850/"><img src="https://img.icons8.com/ios-filled/50/000000/linkedin.png" alt="LinkedIn" width="32" height="32" title="LinkedIn" /></a>
              </p>
              <hr />
              <h1><a href="/research">Research</a> | <a href="/portfolio">Portfolio</a></h1>
        </Col>
        </Row>
      </>
    )
  }
}
