import React from "react";
import Header from '../components/header'
import { Unity, useUnityContext } from "react-unity-webgl";

const Tetris = () => {

  const squareSize = 500


  const { unityProvider, isLoaded, loadingProgression, sendMessage } = useUnityContext({
    loaderUrl: "pages/tetris/build/tetris.loader.js",
    dataUrl: "pages/tetris/build/tetris.data.unityweb",
    frameworkUrl: "pages/tetris/build/tetris.framework.js.unityweb",
    codeUrl: "pages/tetris/build/tetris.wasm.unityweb",
  });

  const loadingPercentage = Math.round(loadingProgression * 100);

  function applyEngineParametersAndRestart() {
    sendMessage("Manager", "ApplyEngineParametersAndRestart")
  }

  return (<>
    <div id="wrapper">
    <Header />
    <div id="main">
    <div className="inner">
    <h1>Modular Tetris</h1>

    <div className="align-center">
    <div className="unity-canvas">
    {isLoaded === false && (
                // We'll conditionally render the loading overlay if the Unity
                // Application is not loaded.
      <div className="loading-overlay">
      <p>Loading... ({loadingPercentage}%)</p>
      </div>
      )}
    <Unity unityProvider={unityProvider} style={{ width: squareSize, height: squareSize }} />
    </div>
    </div>

    <br />

    <h2 className="align-center">Controls</h2>
    <div className="row align-center">
    <div className="col-4">
    <select name="demo-category" id="demo-category">
    <option value="">- Category -</option>
    <option value="1">Manufacturing</option>
    <option value="1">Shipping</option>
    <option value="1">Administration</option>
    <option value="1">Human Resources</option>
    </select>
    </div>
    </div>

    <br />

    <h2 class="align-center">Overview</h2>
    <p class="align-center">Modular style Tetris with combiantion mechanics of different games.</p>

    <hr />

    <div class="align-center">
    <a href="/" class="button">Back</a>
    </div>
    </div>
    </div>
    </div>
    </>
    );
};

export default Tetris;
